// bc-desktop-client/src/client/commons/components/SessionFilterSidebarToggle/SessionFilterSidebarToggle.tsx
import React, { useState } from "react";
import { motion, AnimatePresence } from "motion/react";
import { IconFilter } from "../../../../../resources/icons";

import { Component } from "../../../../commons/types/component";
import cn from "../../libs/class-name";
import config from "../../config";
import Icon from "../Icon/Icon";
import CircularButton from "../CircularButton/CircularButton";

interface Props extends Component {
  sidebar: React.ReactNode;
  isOpen?: boolean;
  onToggle?: (isOpen: boolean) => void;
}

const SessionFilterSidebarToggle = ({ 
  classNames = [], 
  sidebar, 
  isOpen: externalIsOpen, 
  onToggle 
}: Props) => {
  // If isOpen is controlled externally, use that, otherwise manage state internally
  const [internalIsOpen, setInternalIsOpen] = useState(false);
  const isOpen = externalIsOpen !== undefined ? externalIsOpen : internalIsOpen;

  const handleToggle = () => {
    const newState = !isOpen;
    setInternalIsOpen(newState);
    if (onToggle) {
      onToggle(newState);
    }
  };

  return (
    <div className={cn("SessionFilterSidebarToggle", [{ isOpen }], classNames)}>
      <AnimatePresence>
        {!isOpen && (
          <motion.div 
            className="SessionFilterSidebarToggle__button"
            initial={{ x: 100 }}
            animate={{ x: 0 }}
            exit={{ x: 100 }}
            transition={{ 
              type: "tween", 
              ease: "easeOut", 
              duration: config.shared.transitionsDurations.fast 
            }}
          >
            <CircularButton 
              variant="primary" 
              onClick={handleToggle}
              ariaLabel="Open filter sidebar"
            >
              <Icon source={IconFilter} />
            </CircularButton>
          </motion.div>
        )}
      </AnimatePresence>

      <AnimatePresence>
        {isOpen && (
          <motion.div 
            className="SessionFilterSidebarToggle__sidebar"
            initial={{ x: "100%" }}
            animate={{ x: 0 }}
            exit={{ x: "100%" }}
            transition={{ 
              type: "tween", 
              ease: "easeOut", 
              duration: config.shared.transitionsDurations.fast 
            }}
          >
            <div className="SessionFilterSidebarToggle__sidebarContent">
              {sidebar}
            </div>
            <div className="SessionFilterSidebarToggle__sidebarClose">
              <CircularButton 
                variant="primary" 
                onClick={handleToggle}
                ariaLabel="Close filter sidebar"
              >
                <Icon source={IconFilter} />
              </CircularButton>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default SessionFilterSidebarToggle;
