<new_text>import { parse } from "@std/flags";
import { generateSchemaCommand } from "./cli/generate-schema.ts";

const args = parse(Deno.args);
const command = args._[0];

switch (command) {
    case "generate-schema":
        await generateSchemaCommand();
        break;
