export interface SettingsPageProps {
  headerLeft?: React.ReactNode;
  showHelp?: () => void;
  hideHelp?: () => void;
  isHelpVisible?: boolean;
  parentPathName?: string; // e.g. "settings"
  parentLabel?: string; // e.g. "Händlerbereich"
}

const mapStateToProps = (state: State) => {
  const env = selectInitializedEnv(state);
  const settings = selectInitializedSettings(state);
  const customerGroup = getCustomerGroupConfig(env.customerGroup);

  return {
    env,
    features: settings.features,
    featuresMeta: settings.featuresMeta,
    customerGroup,
    toastAlignment: state.toasts.toastAlignment,
    isToolBikeFinancialCalculatorAvailable: selectIsToolAvailable(state, ToolKey.BikeFinancialCalculator, env.locale),
    isToolBikeFrameSizeCalculatorAvailable: selectIsToolAvailable(state, ToolKey.BikeFrameSizeCalculator, env.locale),
    isToolBikepriceCalculatorAvailable: selectIsToolAvailable(state, ToolKey.BikepriceCalculator, env.locale)
  };
};

const mapDispatchToProps = {
  onPush: push,
  setDealerAuthorized: actions.auth.setDealerAuthorized,
  setToastAlignment: actions.toasts.setToastAlignment
};

const connector = connect(mapStateToProps, mapDispatchToProps);

type Props = ConnectedProps<typeof connector> & SettingsPageProps;

interface RouteDefinition {
  path: string;
  component: React.ComponentType<any>;
  exact?: boolean;
  withoutSettingsLayout?: boolean;
}

