const findMatchingRouteDefinition = (pathname: string) =>
  routes.find((route: RouteDefinition) => {
    const match = matchPath(pathname, route);
    return !!match;
  });

const Settings = ({
  onPush,
  toastAlignment,
  setToastAlignment,
  setDealerAuthorized,
  env,
  features,
  featuresMeta,
  customerGroup,
  isToolBikeFinancialCalculatorAvailable,
  isToolBikeFrameSizeCalculatorAvailable,
  isToolBikepriceCalculatorAvailable
}: Props) => {
  const activeBrands = useActiveBrands();
  const { t } = useTranslation(["kiosk"]);
  const [isAuthorized, setAuthorization] = React.useState(false);
  const activeRef = React.useRef<HTMLAnchorElement>(null);

  const location = useLocation();
  const isHelpVisible = qs.parse(location.search, { ignoreQueryPrefix: true }).help === "true";

  const { deviceType } = env;

  const isVideoConferenceVisible = featuresMeta.videoConference.visible;
  const isMailVisible = featuresMeta.mail.visible;
  const isCustomAssortmentVisible = featuresMeta.customAssortment.visible;
  const isWawiVisible = featuresMeta.wawi.visible;
  const isMobileVisible = featuresMeta.mobile.visible;

  const featuresVisible = {
    videoConference: features.videoConferenceEnabled || isVideoConferenceVisible,
    mail: features.mailEnabled || isMailVisible,
    customAssortment: features.customAssortmentEnabled || isCustomAssortmentVisible,
    wawi: features.wawiEnabled || isWawiVisible,
    mobile: features.mobileEnabled || isMobileVisible
  };

  const externalToolsAvailable = externalTools.reduce(
    (aggr, toolConfig) => ({
      ...aggr,
      [toolConfig.key]: isExternalToolAvailable(toolConfig, env, customerGroup, features, activeBrands.brandKeys)
    }),
    {} as Record<ExternalToolKey, boolean>
  );

  const updateToastLayout = React.useCallback(() => {
    let nextToastAlignment = toastAlignment;
    // Find matching route definition to set correct ToastLayout
    const matchingRouteDefinition = findMatchingRouteDefinition(location.pathname);

    if (!isAuthorized || matchingRouteDefinition?.withoutSettingsLayout) {
      nextToastAlignment = "center";
    } else if (isHelpVisible) {
      nextToastAlignment = "sidebar-right";
    } else {
      nextToastAlignment = "sidebar-left";
    }

    if (nextToastAlignment !== toastAlignment) {
      setToastAlignment(nextToastAlignment);
    }
  }, [location.pathname, isAuthorized, isHelpVisible, setToastAlignment, toastAlignment]);

  React.useEffect(() => {
    // Called on mount and whenever dependencies of updateToastLayout change
    updateToastLayout();
  }, [updateToastLayout]);

  useOnUnmount(() => {
    setDealerAuthorized(false);
    setToastAlignment("center");
  });

  useRedirectOnIdle(ROUTES.NAVIGATION.INDEX, {
    timeout: config.kiosk.timeoutInMsBeforeRedirectFromIdleSettings,
    toastOptions: {
      title: t("kiosk:settings.redirectedToMainMenuShortlyTitle"),
      actionLabel: t("kiosk:settings.cancelRedirectButton"),
      kind: "default",
      icon: icons.IconSmallHome
    }
  });

  if (!isAuthorized) {
    return <AuthPage setAuthorization={setAuthorization} allowMasterPin />;
  }

  const showHelp = () => {
    onPush(overwriteQueryInPath(location, { help: "true" }));
  };

  const hideHelp = () => {
    onPush(overwriteQueryInPath(location, { help: "false" }));
  };

  const handleLinkClick = (event: React.MouseEvent<HTMLElement>, href: string) => {
    onPush(href);
  };

  const areToolsAvailable =
    isToolBikepriceCalculatorAvailable ||
    isToolBikeFinancialCalculatorAvailable ||
    isToolBikeFinancialCalculatorAvailable;

  const renderExternalTools = (): React.ReactNode => {
    const navigationItems = (
      <>
        {externalToolsAvailable[ExternalToolKey.Adac] && (
          <Route
            path={ROUTES.SETTINGS.THIRD_PARTY_ADAC}
            exact
            children={({ match }: RouteChildrenProps) => (
              <SidebarNavigationLink
                href={ROUTES.SETTINGS.THIRD_PARTY_ADAC}
                onClick={handleLinkClick}
                active={!!match}
                activeRef={!!match ? activeRef : undefined}
              >
                ADAC Fahrradversicherung
              </SidebarNavigationLink>
            )}
          />
        )}
        {externalToolsAvailable[ExternalToolKey.Alteos] && (
          <Route
            path={ROUTES.SETTINGS.THIRD_PARTY_ALTEOS}
            exact
            children={({ match }: RouteChildrenProps) => (
              <SidebarNavigationLink
                href={ROUTES.SETTINGS.THIRD_PARTY_ALTEOS}
                onClick={handleLinkClick}
                active={!!match}
                activeRef={!!match ? activeRef : undefined}
              >
                Alteos E-Bike-Versicherung
              </SidebarNavigationLink>
            )}
          />
        )}
        {externalToolsAvailable[ExternalToolKey.BcRemoteShop] && (
          <Route
            path={ROUTES.SETTINGS.THIRD_PARTY_BC_REMOTE_SHOP}
            children={({ match }: RouteChildrenProps) => (
              <SidebarNavigationLink
                href={ROUTES.SETTINGS.THIRD_PARTY_BC_REMOTE_SHOP}
                onClick={handleLinkClick}
                active={!!match}
                activeRef={!!match ? activeRef : undefined}
              >
                bc remote B2B Shop
              </SidebarNavigationLink>
            )}
          />
        )}
        {externalToolsAvailable[ExternalToolKey.BicoB2bShop] && (
          <Route
            path={ROUTES.SETTINGS.THIRD_PARTY_BICOSHOP}
            children={({ match }: RouteChildrenProps) => (
              <SidebarNavigationLink
                href={ROUTES.SETTINGS.THIRD_PARTY_BICOSHOP}
                onClick={handleLinkClick}
                active={!!match}
                activeRef={!!match ? activeRef : undefined}
              >
                BIKE&amp;CO B2B Shop (Teile &amp; Zubehör)
              </SidebarNavigationLink>
            )}
          />
        )}
        {externalToolsAvailable[ExternalToolKey.Bikmo] && (
          <Route
            path={ROUTES.SETTINGS.THIRD_PARTY_BIKMO}
            exact
            children={({ match }: RouteChildrenProps) => (
              <SidebarNavigationLink
                href={ROUTES.SETTINGS.THIRD_PARTY_BIKMO}
                onClick={handleLinkClick}
                active={!!match}
                activeRef={!!match ? activeRef : undefined}
              >
                Bikmo
              </SidebarNavigationLink>
            )}
          />
        )}
        {externalToolsAvailable[ExternalToolKey.Enra] && (
          <Route
            path={ROUTES.SETTINGS.THIRD_PARTY_ENRA}
