              >
                Body Scanning CRM
              </SidebarNavigationLink>
            )}
          />
        )}
        {externalToolsAvailable[ExternalToolKey.Smartfit] && (
          <Route
            path={ROUTES.SETTINGS.THIRD_PARTY_BODYSIZING}
            exact
            children={({ match }: RouteChildrenProps) => (
              <SidebarNavigationLink
                href={ROUTES.SETTINGS.THIRD_PARTY_BODYSIZING}
                onClick={handleLinkClick}
                active={!!match}
                activeRef={!!match ? activeRef : undefined}
              >
                Bikesizing by Smartfit&nbsp;&nbsp;
                <Badge size="s" variant={!match ? "accent" : "primary"} label={t("kiosk:defaults.addOn")} />
              </SidebarNavigationLink>
            )}
          />
        )}
        {externalToolsAvailable[ExternalToolKey.SqlabProfiler] && (
          <Route
            path={ROUTES.SETTINGS.THIRD_PARTY_SQLAB_PROFILER}
            exact
            children={({ match }: RouteChildrenProps) => (
              <SidebarNavigationLink
                href={ROUTES.SETTINGS.THIRD_PARTY_SQLAB_PROFILER}
                onClick={handleLinkClick}
                active={!!match}
                activeRef={!!match ? activeRef : undefined}
              >
                SQlab Profiler
              </SidebarNavigationLink>
            )}
          />
        )}
        {externalToolsAvailable[ExternalToolKey.Wertgarantie] && (
          <Route
