<new_text>const Settings = (props: Props) => {
  const dispatch = useDispatch();
  const envData = useEnv();
  const settingsData = useSettings();
  const toastAlignment = useSelector(state => state.toasts.toastAlignment);

  const env = envData.query.data!;
  const customerGroup = envData.customerGroupConfig!;
  const settings = settingsData.query.data!;
  const features = settings.features;
  const featuresMeta = settings.featuresMeta;

  const isToolBikeFinancialCalculatorAvailable = useSelector(state => 
    selectIsToolAvailable(state, ToolKey.BikeFinancialCalculator, env.locale));
  const isToolBikeFrameSizeCalculatorAvailable = useSelector(state => 
    selectIsToolAvailable(state, ToolKey.BikeFrameSizeCalculator, env.locale));
  const isToolBikepriceCalculatorAvailable = useSelector(state => 
    selectIsToolAvailable(state, ToolKey.BikepriceCalculator, env.locale));

  const onPush = (path: string) => dispatch(push(path));
  const setToastAlignment = (alignment: string) => dispatch(actions.toasts.setToastAlignment(alignment));
  const setDealerAuthorized = (value: boolean) => dispatch(actions.auth.setDealerAuthorized(value));</new_text>
